// src/GuitarTable/queryFunctions.tsx
export async function getGuitars() {
  const response = await fetch("http://localhost:8000/guitars");
  const data = await response.json();
  return data;
}

// src/GuitarTable/queries.ts
import { useQuery } from "react-query";
import { getGuitars } from "./queryFunctions";
export const useGuitars = () => {
  return useQuery("guitars", getGuitars);
};

// src/GuitarTable/GuitarTable.tsx
import "ag-grid-community/styles//ag-grid.css";
import "ag-grid-community/styles//ag-theme-alpine.css";
import { AgGridReact } from "ag-grid-react";
import { columnDefs } from "./columnDefs";
import { useGuitars } from "./queries";
const GuitarTable = () => {
  const { data } = useGuitars();
  return (
    <div>
      <h1>Max's Guitars</h1>
      <div
        className="ag-theme-alpine"
        style={{
          height: "500px",
          width: "600px",
        }}
      >
        <AgGridReact columnDefs={columnDefs} rowData={data} />
      </div>
    </div>
  );
};
export default GuitarTable;
